mapper.select:
SELECT id, name, birth_year as birthY
       , birth_month as birthM , birth_day as birthD, sex
FROM USER_DATA WHERE id = #{id}

mapper.update:
UPDATE USER_DATA SET name = #{name}, birth_year = #{birthY}
    , birth_month = #{birthM}, birth_day = #{birthD}, sex = #{sex}
WHERE id = #{id}
